class MyArticle extends HTMLElement {
  constructor(){
    super();
    this.attachShadow({mode: 'open'});
    this.size = '400px';
    this.render();
  }

  render(){
    const t = document.createElement('template');
    t.innerHTML = `
      <style>
        :host {
          color: black; 
        }
        article {
          padding: 2em;
        }
        img {
          width: ${this.size};
        }
        figcaption {
          width: ${this.size};
        }
      </style>
      <article>
        <figure>
            <img id='image' src="data:image/png;base64,"
                alt="Article on sale">
            <figcaption id='caption'><span id='description'>Article on sale</span></figcaption>
        </figure>
      </article>
    `;

    this.shadowRoot.appendChild(t.content.cloneNode(true));
  }

  // manage custom element attributes
  static get observedAttributes(){
    return [
      'image', 'description', 
      // 'isThumbnail'
    ];
  }

  attributeChangedCallback(attrName, oldValue, newValue) {
    if (attrName === 'image') {
      this.shadowRoot.getElementById(attrName).setAttribute('src', newValue);
    } else if (attrName === 'description') {
      this.shadowRoot.getElementById(attrName).innerText = newValue;
    // } else if (attrName === 'isThumbnail') {
    //   const width = newValue === 'true' ? '200px' : '400px';
    //   this.shadowRoot.getElementById('image').style.width = width;
    //   this.shadowRoot.getElementById('caption').style.width = width;
    }
  }
  
}

export {MyArticle};

